import axios from "axios";
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

const UpdateClient = (props) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [nama, setNama] = useState("");
  const [gambar, setGambar] = useState("");
  const [telepon, setTelepon] = useState("");
  const [email, setEmail] = useState("");

  const onChangeFile = (e) => {
    e.preventDefault();
    setGambar(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    const formData = new FormData();
    formData.append("nama", nama);
    formData.append("telepon", telepon);
    formData.append("email", email);
    formData.append("gambar", gambar);

    axios
      .put(
        `http://localhost:4000/api/clients/update/${props.match.params.id}`,
        formData
      )
      .then((res) => alert(res.data), history.push("/clients"))
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    axios
      .get(`http://localhost:4000/api/clients/${props.match.params.id}`)
      .then((res) => [
        setNama(res.data.nama),
        setTelepon(res.data.telepon),
        setEmail(res.data.email),
        setGambar(res.data.gambar),
      ])
      .catch((error) => console.log(error));
  }, []);

  return (
    <>
      <Modal.Header closeButton>
        <Modal.Title>Tambah Data Client</Modal.Title>
      </Modal.Header>
      <form onSubmit={changeOnClick} encType="multipart/form-data">
        <Modal.Body>
          <div className="form-group">
            <label htmlFor="nama" className="form-label">
              Nama
            </label>
            <input
              required={true}
              type="text"
              value={nama}
              onChange={(e) => setNama(e.target.value)}
              className="form-control text-center"
              placeholder="nama"
            />
          </div>
          <div className="form-group">
            <label htmlFor="telepon" className="form-label">
              Telepon
            </label>
            <input
              required={true}
              type="text"
              value={telepon}
              onChange={(e) => setTelepon(e.target.value)}
              className="form-control"
              placeholder="telepon"
            />
          </div>
          <div className="form-group">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              required={true}
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control"
              placeholder="email"
            />
          </div>
          <div className="form-group">
            <label htmlFor="file">Pilih Gambar</label>
            <div>
              <input
                required={true}
                type="file"
                filename="gambar"
                onChange={onChangeFile}
              />
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" type="submit">
            Tambah
          </Button>
        </Modal.Footer>
      </form>
    </>
  );
};

export default UpdateClient;
