import axios from "axios";
import React, { useState, useEffect, useCallback } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Footer from "../../imports/Footer";
import Navbar from "../../imports/Navbar";
import Sidebar from "../../imports/Sidebar";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import AddClient from "./AddClient";
import UpdateClient from "./UpdateClient";

const Clients = ({ posts, props }) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = useCallback(() => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  });
  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [client, setClient] = useState([]);
  // Delete by iD
  const deleteClient = (id) => {
    axios
      .delete(`http://localhost:4000/api/clients/${id}`)
      .then((res) => alert(res.data));
    setClient(client.filter((elem) => elem._id !== id));
  };

  const [show, setShow] = useState(false);
  const [show2, setShow2] = useState(false);
  const handleClose = () => setShow(false);
  const handleClose2 = () => setShow2(false);
  const handleShow = () => setShow(true);
  const handleShow2 = () => setShow2(true);

  const [nama, setNama] = useState("");
  const [filename, setFileName] = useState("");

  const onChangeFile = (e) => {
    setFileName(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("nama", nama);
    formData.append("gambar", filename);

    axios
      .put(
        `http://localhost:4000/api/clients/update/${props.match.params.id}`,
        formData
      )
      .then((res) => alert(res.data), history.push("/clients"))
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <Navbar />
      <div id="layoutSidenav">
        <Sidebar />
        <div id="layoutSidenav_content" className="mt-4">
          <main>
            <div className="container-fluid">
              <ol className="breadcrumb mb-4 mt-5">
                <li className="breadcrumb-item active">Data Clients</li>
              </ol>
              <div className="card mb-4">
                <div className="card-header">
                  <Button variant="success" onClick={handleShow}>
                    Tambah Data
                  </Button>
                </div>
                <div className="card-body">
                  <div className="table-responsive">
                    <table
                      className="table table-bordered"
                      id="dataTable"
                      width="100%"
                      cellSpacing="0"
                    >
                      <thead>
                        <tr className="text-center">
                          <th width="100">GAMBAR</th>
                          <th>NAMA</th>
                          <th colSpan="2">AKSI</th>
                        </tr>
                      </thead>
                      <tbody>
                        {posts.map((client, key) => (
                          <tr key={key} className="text-center">
                            <td>
                              <img
                                width="50"
                                height="50"
                                src={
                                  `http://localhost:4000/uploads/clients/` +
                                  client.gambar
                                }
                                alt="..."
                              />
                            </td>
                            <td>
                              <p>{client.nama}</p>
                            </td>

                            <td width="50">
                              <Button variant="primary" onClick={handleShow2}>
                                <i className="fas fa-edit"></i>
                              </Button>
                            </td>
                            <td width="50">
                              <button
                                className="btn btn-danger btn-circle"
                                onClick={() => deleteClient(client._id)}
                                type="submit"
                              >
                                <i className="fas fa-trash"></i>
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </main>
          {/* <!-- Modal Tambah Clients--> */}
          <Modal show={show} onHide={handleClose} animation={true}>
            <AddClient />
          </Modal>

          {/* <!-- Modal Update Clients--> */}
          <Modal show={show2} onHide={handleClose2} animation={true}>
            <UpdateClient />
          </Modal>
          <Footer />
        </div>
      </div>
    </div>
  );
};

export default Clients;
