import axios from "axios";
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Logout from "../../imports/Logout";

const AddProject = () => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };
  const [judul, setJudul] = useState("");
  const [deskripsi, setDeskripsi] = useState("");
  const [filename, setFileName] = useState("");

  const onChangeFile = (e) => {
    setFileName(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    const formData = new FormData();

    formData.append("judul", judul);
    formData.append("deskripsi", deskripsi);
    formData.append("gambar", filename);

    axios
      .post("http://localhost:4000/api/projects/add", formData)
      .then((res) => alert(res.data), history.push("/projects"))
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <>
        <Modal.Header closeButton>
          <Modal.Title>Tambah Data Client</Modal.Title>
        </Modal.Header>
        <form onSubmit={changeOnClick} encType="multipart/form-data">
          <Modal.Body>
            <div className="form-group">
              <label htmlFor="judul" className="form-label">
                Judul
              </label>
              <input
                required={true}
                type="text"
                value={judul}
                onChange={(e) => setJudul(e.target.value)}
                className="form-control"
                placeholder="judul"
              />
            </div>

            <div className="form-group">
              <label htmlFor="deskripsi" className="form-label">
                Deskripsi
              </label>
              <input
                required={true}
                type="text"
                value={deskripsi}
                onChange={(e) => setDeskripsi(e.target.value)}
                className="form-control"
                placeholder="deskripsi"
              />
            </div>

            <div className="form-group">
              <label htmlFor="file">Pilih Gambar</label>
              <div>
                <input
                  required={true}
                  type="file"
                  filename="gambar"
                  onChange={onChangeFile}
                />
              </div>
            </div>

            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save Changes
            </Button>
          </Modal.Footer>
        </form>
      </>

      <div className="card mb-4">
        <div className="container mb-3 mt-3">
          <form onSubmit={changeOnClick} encType="multipart/form-data"></form>
        </div>
      </div>
    </div>
  );
};

export default AddProject;
