import React, { useState, useEffect } from "react";
import axios from "axios";

const UpdateClient = (props) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [nama, setNama] = useState("");
  const [message, setMessage] = useState("");
  const [fileName, setFileName] = useState("");

  const onChangeFile = (e) => {
    setFileName(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    const formData = new FormData();

    formData.append("nama", nama);
    formData.append("image", fileName);

    axios
      .put(`/clients/update/${props.match.params.id}`, formData)
      .then((res) => setMessage(res.data))
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    axios
      .get(`/clients/${props.match.params.id}`)
      .then((res) => [setNama(res.data.nama), setFileName(res.data.image)])
      .catch((error) => console.log(error));
  }, []);

  return (
    <div className="container">
      <h1>Update Article</h1>
      <span> {message} </span>
      <form onSubmit={changeOnClick} encType="multipart/form-data">
        <div className="form-group">
          <label htmlFor="nama" className="form-label">
            Nama
          </label>
          <input
            type="text"
            value={nama}
            onChange={(e) => setNama(e.target.value)}
            className="form-control"
            placeholder="nama"
          />
        </div>
        <div className="form-group">
          <label htmlFor="file">pilih gambar</label>
          <input type="file" fileName="gambar" onChange={onChangeFile} />
        </div>

        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default UpdateClient;
