import axios from "axios";
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Logout from "../../imports/Logout";

const Teams = ({ posts }) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [team, setTeam] = useState([]);
  // Delete by iD

  const deleteTeam = (id) => {
    axios
      .delete(`http://localhost:4000/api/teams/${id}`)
      .then((res) => alert(res.data));
    setTeam(team.filter((elem) => elem._id !== id));
  };

  return (
    <div>
      <nav className="sb-topnav navbar navbar-expand navbar-dark bg-dark ">
        <a className="navbar-brand" href="/#">
          Admin
        </a>

        <form className="d-none d-md-inline-block form-inline ml-auto mr-0 mr-md-3 my-2 my-md-0">
          <div className="input-group">
            <input
              className="form-control"
              type="text"
              placeholder="Search for..."
              aria-label="Search"
              aria-describedby="basic-addon2"
            />
            <div className="input-group-append">
              <button className="btn btn-primary" type="button">
                <i className="fas fa-search"></i>
              </button>
            </div>
          </div>
        </form>

        <ul className="navbar-nav ml-auto ml-md-0">
          <li className="nav-item dropdown">
            <a
              className="nav-link dropdown-toggle"
              id="userDropdown"
              href="/#"
              role="button"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              <i className="fas fa-user fa-fw"></i>
            </a>
            <div
              className="dropdown-menu dropdown-menu-right"
              aria-labelledby="userDropdown"
            >
              <div>
                {posts.map((user) => (
                  <Link
                    className="dropdown-item "
                    to={{
                      pathname: `/update-user/${user._id}`,
                    }}
                  >
                    <div className="text-center">Update User</div>
                  </Link>
                ))}
              </div>
              <Logout className="dropdown-item" onLogout={logout}></Logout>
            </div>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Teams;
