import axios from "axios";
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Logout from "../../imports/Logout";

const UpdateClient = (props) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [nama, setNama] = useState("");
  const [gambar, setGambar] = useState("");
  const [telepon, setTelepon] = useState("");
  const [email, setEmail] = useState("");

  const onChangeFile = (e) => {
    e.preventDefault();
    setGambar(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    const formData = new FormData();
    formData.append("nama", nama);
    formData.append("telepon", telepon);
    formData.append("email", email);
    formData.append("gambar", gambar);

    axios
      .put(
        `http://localhost:4000/api/clients/update/${props.match.params.id}`,
        formData
      )
      .then((res) => setMessage(res.data))
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    axios
      .get(`http://localhost:4000/api/clients/${props.match.params.id}`)
      .then((res) => [
        setNama(res.data.nama),
        setTelepon(res.data.telepon),
        setEmail(res.data.email),
        setGambar(res.data.gambar),
      ])
      .catch((error) => console.log(error));
  }, []);

  return (
    <div>
      <nav className="sb-topnav navbar navbar-expand navbar-dark bg-dark">
        <a className="navbar-brand" href="/#">
          Admin
        </a>

        <form className="d-none d-md-inline-block form-inline ml-auto mr-0 mr-md-3 my-2 my-md-0">
          <div className="input-group">
            <input
              className="form-control"
              type="text"
              placeholder="Search for..."
              aria-label="Search"
              aria-describedby="basic-addon2"
            />
            <div className="input-group-append">
              <button className="btn btn-primary" type="button">
                <i className="fas fa-search"></i>
              </button>
            </div>
          </div>
        </form>

        <ul className="navbar-nav ml-auto ml-md-0">
          <li className="nav-item dropdown">
            <a
              className="nav-link dropdown-toggle"
              id="userDropdown"
              href="/#"
              role="button"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              <i className="fas fa-user fa-fw"></i>
            </a>
            <div
              className="dropdown-menu dropdown-menu-right"
              aria-labelledby="userDropdown"
            >
              <Link className="dropdown-item text-center" to="/user">
                User
              </Link>
              <Logout class="dropdown-item" onLogout={logout}></Logout>
            </div>
          </li>
        </ul>
      </nav>
      <div id="layoutSidenav">
        <div id="layoutSidenav_nav">
          <nav
            className="sb-sidenav accordion sb-sidenav-dark"
            id="sidenavAccordion"
          >
            <div className="sb-sidenav-menu">
              <div className="nav">
                <div className="sb-sidenav-menu-heading">Core</div>
                <Link className="nav-link" to={{ pathname: "/" }}>
                  <div className="sb-nav-link-icon">
                    <i className="fas fa-tachometer-alt"></i>
                  </div>
                  Dashboard
                </Link>
                <div className="sb-sidenav-menu-heading">Interface</div>
              </div>

              <Link className="nav-link" to={{ pathname: `/clients` }}>
                <i className="fas fa-fw fa-table"></i>
                <span> Clients</span>
              </Link>

              <Link className="nav-link" to={{ pathname: `/teams` }}>
                <i className="fas fa-fw fa-table"></i>
                <span> Teams</span>
              </Link>

              <Link className="nav-link" to={{ pathname: `/projects` }}>
                <i className="fas fa-fw fa-table"></i>
                <span> Projects</span>
              </Link>
            </div>
            <div className="sb-sidenav-footer">
              <div className="small">Logged in as:</div>
              Admin
            </div>
          </nav>
        </div>
        <div id="layoutSidenav_content">
          <main>
            <div className="container-fluid">
              <ol className="breadcrumb mb-4 mt-3">
                <li className="breadcrumb-item active">Update Data Client</li>
              </ol>

              <div className="card mb-4">
                <div className="container mt-3 mb-3">
                  <form onSubmit={changeOnClick} encType="multipart/form-data">
                    <div className="form-group">
                      <label htmlFor="nama" className="form-label">
                        Nama
                      </label>
                      <input
                        required={true}
                        type="text"
                        value={nama}
                        onChange={(e) => setNama(e.target.value)}
                        className="form-control"
                        placeholder="nama"
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="telepon" className="form-label">
                        Telepon
                      </label>
                      <input
                        required={true}
                        type="text"
                        value={telepon}
                        onChange={(e) => setTelepon(e.target.value)}
                        className="form-control"
                        placeholder="telepon"
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="email" className="form-label">
                        Email
                      </label>
                      <input
                        required={true}
                        type="text"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="form-control"
                        placeholder="email"
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="file">Pilih Gambar</label>
                      <div>
                        <input
                          required={true}
