import axios from "axios";
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Logout from "../../imports/Logout";

const Teams = ({ posts }) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [team, setTeam] = useState([]);
  // Delete by iD

  const deleteTeam = (id) => {
    axios
      .delete(`http://localhost:4000/api/teams/${id}`)
      .then((res) => alert(res.data));
    setTeam(team.filter((elem) => elem._id !== id));
  };

  return (
    <div>
      <div>
        {posts.map((user) => (
          <Link
            className="dropdown-item "
            to={{
              pathname: `/update-user/${user._id}`,
            }}
          >
            <div className="text-center">Update User</div>
          </Link>
        ))}
      </div>
      <Logout className="dropdown-item" onLogout={logout}></Logout>
    </div>
  );
};

export default Teams;
