import axios from "axios";
import React, { useState, useEffect, useCallback } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Footer from "../../imports/Footer";
import Navbar from "../../imports/Navbar";
import Sidebar from "../../imports/Sidebar";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

const Clients = ({ posts }) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = useCallback(() => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  });
  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [client, setClient] = useState([]);
  // Delete by iD
  const deleteClient = (id) => {
    axios
      .delete(`http://localhost:4000/api/clients/${id}`)
      .then((res) => alert(res.data));
    setClient(client.filter((elem) => elem._id !== id));
  };

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [nama, setNama] = useState("");
  const [telepon, setTelepon] = useState("");
  const [email, setEmail] = useState("");
  const [filename, setFileName] = useState("");

  const onChangeFile = (e) => {
    setFileName(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("nama", nama);
    formData.append("telepon", telepon);
    formData.append("email", email);
    formData.append("gambar", filename);

    axios
      .post("http://localhost:4000/api/clients/add", formData)
      .then((res) => alert(res.data), history.push("/clients"))
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <Navbar />
      <div id="layoutSidenav">
        <Sidebar />
        <div id="layoutSidenav_content" className="mt-4">
          <main>
            <div className="container-fluid">
              <ol className="breadcrumb mb-4 mt-5">
                <li className="breadcrumb-item active">Data Clients</li>
              </ol>
              <div className="card mb-4">
                <div className="card-header">
                  <Button variant="success" onClick={handleShow}>
                    Tambah Data
                  </Button>
                </div>
                <div className="card-body">
                  <div className="table-responsive">
                    <table
                      className="table table-bordered"
                      id="dataTable"
                      width="100%"
                      cellSpacing="0"
                    >
                      <thead>
                        <tr className="text-center">
                          <th width="100">GAMBAR</th>
                          <th>NAMA</th>
                          <th>TELEPON</th>
                          <th>EMAIL</th>
                          <th colSpan="2">AKSI</th>
                        </tr>
                      </thead>
                      <tbody>
                        {posts.map((client, key) => (
                          <tr key={key} className="text-center">
                            <td>
                              <img
                                width="50"
                                height="50"
                                src={
                                  `http://localhost:4000/uploads/clients/` +
                                  client.gambar
                                }
                                alt="..."
                              />
                            </td>
                            <td>
                              <p>{client.nama}</p>
                            </td>
                            <td>
                              <p>{client.telepon}</p>
                            </td>
                            <td>
                              <p>{client.email}</p>
                            </td>
                            <td width="50">
                              <Link
                                to={{
                                  pathname: `/update-client/${client._id}`,
                                }}
                              >
                                <button
                                  className="btn btn-primary btn-circle"
                                  type="submit"
                                >
                                  <i className="fas fa-edit"></i>
                                </button>
                              </Link>
                            </td>
                            <td width="50">
                              <button
                                className="btn btn-danger btn-circle"
                                onClick={() => deleteClient(client._id)}
                                type="submit"
                              >
                                <i className="fas fa-trash"></i>
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </main>
          {/* <!-- Modal Clients--> */}

          <Modal show={show} onHide={handleClose} animation={false}>
            <Modal.Header closeButton className="close">
              <Modal.Title>Tambah Data Client</Modal.Title>
            </Modal.Header>
            <form onSubmit={changeOnClick} encType="multipart/form-data">
              <Modal.Body>
                <div className="form-group">
                  <label htmlFor="nama" className="form-label">
                    Nama
                  </label>
                  <input
                    required={true}
                    type="text"
                    value={nama}
                    onChange={(e) => setNama(e.target.value)}
                    className="form-control"
                    placeholder="nama"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="telepon" className="form-label">
                    Telepon
                  </label>
                  <input
                    required={true}
                    type="text"
                    value={telepon}
                    onChange={(e) => setTelepon(e.target.value)}
                    className="form-control"
                    placeholder="telepon"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email" className="form-label">
                    Email
                  </label>
                  <input
                    required={true}
                    type="text"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="form-control"
                    placeholder="email"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="file">Pilih Gambar</label>
                  <div>
                    <input
                      required={true}
                      type="file"
                      filename="gambar"
                      onChange={onChangeFile}
                    />
                  </div>
                </div>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="primary" type="submit">
                  Save Changes
                </Button>
              </Modal.Footer>
            </form>
          </Modal>
          <Footer />
        </div>
      </div>
    </div>
  );
};

export default Clients;
