import React, { useState } from "react";
import RegistrationForm from "../imports/RegistrationForm";
import BaseLogin from "../imports/BaseLogin";
import { registerAction } from "../../container/actions";
import { useDispatch } from "react-redux";

function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordCheck, setPasswordCheck] = useState("");

  const dispatch = useDispatch();

  const handleSubmit = (event) => {
    event.preventDefault("");

    const newUser = {
      username,
      email,
      password,
      passwordCheck,
    };
    const user = {
      username: "admin",
      email: "admin@gmail.com",
      password: "admin123",
      passwordCheck: "admin123",
    };

    const validate = dispatch(registerAction(user));
    validate
      .then((data) => {
        console.log(data);
      })
      .catch((error) => console.log(error));

    // console.log(newUser);
  };

  let registerData = {
    handleSubmit,
    setUsername,
    setEmail,
    setPassword,
    setPasswordCheck,
  };

  return (
    <div>
      <BaseLogin />
      <RegistrationForm registerState={registerData} />
    </div>
  );
}

export default Register;
