import axios from "axios";
import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Logout from "../../imports/Logout";

const UpdateUser = (props) => {
  // const history = useHistory();
  const dispatch = useDispatch();

  // const route = () => {
  //   const token = localStorage.getItem("x-access-token");
  //   return token ? true : false;
  // };

  // useEffect(() => {
  //   if (!route()) {
  //     history.push("/login");
  //   }
  // }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const changeOnClick = (e) => {
    const formData = new FormData();

    formData.append("username", username);
    formData.append("password", password);

    axios
      .put(
        `http://localhost:4000/api/user/update/${props.match.params.id}`,
        formData
      )
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    axios
      .get(`http://localhost:4000/api/user/${props.match.params.id}`)
      .then((res) => [
        setUsername(res.data.username),
        setPassword(res.data.password),
      ])
      .catch((error) => console.log(error));
  }, []);

  return (
    <div>
      <nav className="sb-topnav navbar navbar-expand navbar-dark bg-dark">
        <a className="navbar-brand" href="/#">
          Admin
        </a>

        <form className="d-none d-md-inline-block form-inline ml-auto mr-0 mr-md-3 my-2 my-md-0">
          <div className="input-group">
            <input
              className="form-control"
              type="text"
              placeholder="Search for..."
              aria-label="Search"
              aria-describedby="basic-addon2"
            />
            <div className="input-group-append">
              <button className="btn btn-primary" type="button">
                <i className="fas fa-search"></i>
              </button>
            </div>
          </div>
        </form>

        <ul className="navbar-nav ml-auto ml-md-0">
          <li className="nav-item dropdown">
            <a
              className="nav-link dropdown-toggle"
              id="userDropdown"
              href="/#"
              role="button"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              <i className="fas fa-user fa-fw"></i>
            </a>
            <div
              className="dropdown-menu dropdown-menu-right"
              aria-labelledby="userDropdown"
            >
              <Logout className="dropdown-item" onLogout={logout}></Logout>
            </div>
          </li>
        </ul>
      </nav>
      <div id="layoutSidenav">
        <div id="layoutSidenav_nav">
          <nav
            className="sb-sidenav accordion sb-sidenav-dark"
            id="sidenavAccordion"
          >
            <div className="sb-sidenav-menu">
              <div className="nav">
                <div className="sb-sidenav-menu-heading">Core</div>
                <Link className="nav-link" to={{ pathname: "/" }}>
                  <div className="sb-nav-link-icon">
                    <i className="fas fa-tachometer-alt"></i>
                  </div>
                  Dashboard
                </Link>
                <div className="sb-sidenav-menu-heading">Interface</div>
              </div>

              <Link className="nav-link" to={{ pathname: `/clients` }}>
                <i className="fas fa-fw fa-table"></i>
                <span> Clients</span>
              </Link>

              <Link className="nav-link" to={{ pathname: `/teams` }}>
                <i className="fas fa-fw fa-table"></i>
                <span> Teams</span>
              </Link>

              <Link className="nav-link" to={{ pathname: `/projects` }}>
                <i className="fas fa-fw fa-table"></i>
                <span> Projects</span>
              </Link>
            </div>
            <div className="sb-sidenav-footer">
              <div className="small">Logged in as:</div>
              Admin
            </div>
          </nav>
        </div>
        <div id="layoutSidenav_content">
          <main>
            <div className="container-fluid">
              <ol className="breadcrumb mb-4 mt-3">
                <li className="breadcrumb-item active">Update Data Team</li>
              </ol>
              <div className="card mb-4">
                <div className="container mt-3 mb-3">
                  <form onSubmit={changeOnClick} encType="multipart/form-data">
                    <div className="form-group">
                      <label htmlFor="username" className="form-label">
                        Username
                      </label>
                      <input
                        required={true}
                        type="text"
                        value="username"
                        onChange={(e) => setUsername(e.target.value)}
                        className="form-control"
                        placeholder="username"
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="password" className="form-label">
                        Password
                      </label>
                      <input
                        required={true}
                        type="text"
                        value="password"
                        onChange={(e) => setPassword(e.target.value)}
                        className="form-control"
                        placeholder="password"
                      />
                    </div>

                    <button type="submit" className="btn btn-primary">
                      Submit
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </main>
          <footer className="py-4 bg-light mt-auto">
            <div className="container-fluid">
              <div className="d-flex align-items-center justify-content-between small">
                <div className="text-muted">
                  Copyright &copy; Your Website 2020
                </div>
                <div>
                  <a href="/#">Privacy Policy</a>
                  &middot;
                  <a href="/#">Terms &amp; Conditions</a>
                </div>
              </div>
            </div>
          </footer>
        </div>
      </div>
    </div>
  );
};

export default UpdateUser;
