import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { logoutAction } from "../../../container/actions";
import Footer from "../../imports/Footer";
import Navbar from "../../imports/Navbar";
import Sidebar from "../../imports/Sidebar";

const AddClient = ({ posts }) => {
  const history = useHistory();
  const dispatch = useDispatch();

  const route = () => {
    const token = localStorage.getItem("x-access-token");
    return token ? true : false;
  };

  useEffect(() => {
    if (!route()) {
      history.push("/login");
    }
  }, [route, history]);

  const logout = () => {
    dispatch(logoutAction());
  };

  const [nama, setNama] = useState("");
  const [telepon, setTelepon] = useState("");
  const [email, setEmail] = useState("");
  const [filename, setFileName] = useState("");

  const onChangeFile = (e) => {
    setFileName(e.target.files[0]);
  };

  const changeOnClick = (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("nama", nama);
    formData.append("telepon", telepon);
    formData.append("email", email);
    formData.append("gambar", filename);

    axios
      .post("http://localhost:4000/api/clients/add", formData)
      .then((res) => alert(res.data), history.push("/clients"))
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <Modal.Header>
        <Modal.Title>Tambah Data Client</Modal.Title>
      </Modal.Header>
      <form onSubmit={changeOnClick} encType="multipart/form-data">
        <Modal.Body>
          <div className="form-group">
            <label htmlFor="nama" className="form-label">
              Nama
            </label>
            <input
              required={true}
              type="text"
              value={nama}
              onChange={(e) => setNama(e.target.value)}
              className="form-control"
              placeholder="nama"
            />
          </div>
          <div className="form-group">
            <label htmlFor="telepon" className="form-label">
              Telepon
            </label>
            <input
              required={true}
              type="text"
              value={telepon}
              onChange={(e) => setTelepon(e.target.value)}
              className="form-control"
              placeholder="telepon"
            />
          </div>
          <div className="form-group">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              required={true}
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control"
              placeholder="email"
            />
          </div>
          <div className="form-group">
            <label htmlFor="file">Pilih Gambar</label>
            <div>
              <input
                required={true}
                type="file"
                filename="gambar"
                onChange={onChangeFile}
              />
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" type="submit">
            Save Changes
          </Button>
        </Modal.Footer>
      </form>
    </>
  );
};

export default AddClient;
