import React, { useState } from "react";
import LoginForm from "../imports/LoginForm";

import { useDispatch, useStore } from "react-redux";
import { loginAction } from "../../container/actions";
import { useHistory } from "react-router-dom";
import BaseLogin from "../imports/BaseLogin";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setError] = useState("");

  const dispatch = useDispatch();
  const history = useHistory();
  const store = useStore();

  // handle submit
  const handleSubmit = (event) => {
    event.preventDefault();

    const userCredential = {
      email,
      password,
    };

    // dispatch({ type: "REGISTER_SUCCESS", payload: "login dispatch" });
    // const userData = { email: "admin", password: "admin123" };
    const login = dispatch(loginAction(userCredential));
    login
      .then(() => history.push("/"))
      .catch((error) => {
        setError(error.err);
        console.log(error.err);
      });
    // console.log(store.getState());
    // console.log(userCredential);
  };

  return (
    <div>
      <LoginForm
        loginState={{
          errorMessage,
          handleSubmit,
          setEmail,
          setPassword,
          setError,
        }}
      />
    </div>
  );
}

export default Login;
